  protected void addCaCertificate(String caCert) {
    tslCertificateSource.addTSLCertificate(getCertFromPEMFormat(caCert));
  }

  protected void addOcspCertificate(String caCert) {
    addOcspCertificate(tslCertificateSource, getCertFromPEMFormat(caCert));
  }

  protected void addTsaCertificate(String caCert) {
    addTsaCertificate(tslCertificateSource, getCertFromPEMFormat(caCert));
  }

  static X509Certificate getCertFromPEMFormat(String pemCert) {
    if (pemCert == null) {
      return null;
    }

    try {
      return getCertFromBytes(pemCert.getBytes());
    } catch (CertificateException e) {
      throw new RuntimeException(e);
    }
  }

  private static X509Certificate getCertFromBytes(byte[] cert) throws CertificateException {
    CertificateFactory certificateFactory = CertificateFactory.getInstance("X.509");

    synchronized (certificateFactory) {
      return (X509Certificate) certificateFactory.generateCertificate(new ByteArrayInputStream(cert));
    }
  }

  private void addOcspCertificate(TSLCertificateSource tslCertificateSource, X509Certificate certificate) {
    ServiceInfo serviceInfo = new ServiceInfo();
    // TODO: check following logic, find correct implementation
    ServiceInfoStatus status = createServiceInfoStatus("http://uri.etsi.org/TrstSvc/Svctype/Certstatus/OCSP/QC", "http://uri.etsi.org/TrstSvc/TrustedList/Svcstatus/undersupervision", certificate.getNotBefore());
    TimeDependentValues<ServiceInfoStatus> tdvStatus = new TimeDependentValues(Arrays.asList(status));
    serviceInfo.setStatus(tdvStatus);

    tslCertificateSource.addCertificate(new CertificateToken(certificate), serviceInfo);
  }

